  1→'use client';
  2→
  3→import React, { useEffect, useState } from 'react';
  4→import { useRouter } from 'next/navigation';
  5→import { LeftNav } from '@/components/layout/LeftNav';
  6→import { Dashboard } from '@/components/dashboard/Dashboard';
  7→import { useFrameStore } from '@/store';
  8→import { useProjectStore } from '@/store/projectStore';
  9→import { useAuthContext } from '@/contexts/AuthContext';
 10→import {
 11→  Dialog,
 12→  DialogContent,
 13→  DialogHeader,
 14→  DialogTitle,
 15→  DialogDescription,
 16→} from '@/components/ui/dialog';
 17→import { Loader2 } from 'lucide-react';
 18→
 19→export default function DashboardPage() {
 20→  const router = useRouter();
 21→  const { user } = useAuthContext();
 22→  const { loadFrames, isLoading, currentSpace, getArchivedFrames } = useFrameStore();
 23→  const { loadProjects, currentProjectId } = useProjectStore();
 24→  const [showSettings, setShowSettings] = useState(false);
 25→
 26→  // Load projects and frames on mount
 27→  useEffect(() => {
 28→    if (user) {
 29→      loadProjects(user.id);
 30→    }
 31→  }, [user, loadProjects]);
 32→
 33→  // Reload frames when project changes
 34→  useEffect(() => {
 35→    loadFrames();
 36→  }, [loadFrames, currentProjectId]);
 37→
 38→  // Redirect to other pages when their space is selected
 39→  useEffect(() => {
 40→    if (currentSpace === 'knowledge') {
 41→      router.push('/knowledge');
 42→    }
 43→  }, [currentSpace, router]);
 44→
 45→  const handleFrameClick = (frameId: string) => {
 46→    router.push(`/frame/${frameId}`);
 47→  };
 48→
 49→  const handleNewFrame = () => {
 50→    router.push('/new');
 51→  };
 52→
 53→  const archivedFrames = getArchivedFrames();
 54→
 55→  return (
 56→    <div className="flex h-screen bg-slate-50">
 57→      {/* Left Navigation */}
 58→      <LeftNav
 59→        onSettingsClick={() => setShowSettings(true)}
 60→      />
 61→
 62→      {/* Main Content */}
 63→      <div className="flex-1 pt-0">
 64→        {currentSpace === 'working' && (
 65→          <Dashboard
 66→            onFrameClick={handleFrameClick}
 67→            onNewFrame={handleNewFrame}
 68→          />
 69→        )}
 70→        {currentSpace === 'archive' && (
 71→          <div className="p-6">
 72→            <h1 className="text-2xl font-semibold text-slate-900 mb-4">Archive</h1>
 73→            {archivedFrames.length === 0 ? (
 74→              <p className="text-slate-500">No archived frames yet.</p>
 75→            ) : (
 76→              <div className="space-y-4">
 77→                {archivedFrames.map((frame) => (
 78→                  <div
 79→                    key={frame.id}
 80→                    onClick={() => handleFrameClick(frame.id)}
 81→                    className="p-4 bg-white rounded-lg border border-slate-200 cursor-pointer hover:shadow-md transition-shadow"
 82→                  >
 83→                    <h3 className="font-medium text-slate-900">
 84→                      {frame.problemStatement || 'Untitled Frame'}
 85→                    </h3>
 86→                    <p className="text-sm text-slate-500 mt-1">
 87→                      Archived - {frame.type}
 88→                    </p>
 89→                  </div>
 90→                ))}
 91→              </div>
 92→            )}
 93→          </div>
 94→        )}
 95→      </div>
 96→
 97→      {/* Settings Modal (placeholder) */}
 98→      <Dialog open={showSettings} onOpenChange={setShowSettings}>
 99→        <DialogContent>
100→          <DialogHeader>
101→            <DialogTitle>Settings</DialogTitle>
102→            <DialogDescription>
103→              Configure your Framer preferences
104→            </DialogDescription>
105→          </DialogHeader>
106→          <div className="py-4 text-sm text-slate-500">
107→            Settings panel coming soon. Backend API is connected.
108→          </div>
109→        </DialogContent>
110→      </Dialog>
111→
112→      {/* Loading Overlay */}
113→      {isLoading && (
114→        <div className="fixed inset-0 bg-white/50 flex items-center justify-center z-50">
115→          <div className="flex items-center gap-2 text-slate-600">
116→            <Loader2 className="h-5 w-5 animate-spin" />
117→            Loading...
118→          </div>
119→        </div>
120→      )}
121→    </div>
122→  );
123→}
