  1→'use client';
  2→
  3→import React, { useRef, useEffect, useState } from 'react';
  4→import { Send, Loader2, User, Bot, RotateCcw } from 'lucide-react';
  5→import { ConversationMessage } from '@/types';
  6→import { Button } from '@/components/ui/button';
  7→import { cn } from '@/lib/utils';
  8→
  9→interface ChatInterfaceProps {
 10→  messages: ConversationMessage[];
 11→  isTyping: boolean;
 12→  onSendMessage: (content: string) => void;
 13→  onRetryMessage?: (messageId: string) => void;
 14→  disabled?: boolean;
 15→  userName?: string;
 16→  botName?: string;
 17→}
 18→
 19→export function ChatInterface({
 20→  messages,
 21→  isTyping,
 22→  onSendMessage,
 23→  onRetryMessage,
 24→  disabled = false,
 25→  userName,
 26→  botName = 'Coach',
 27→}: ChatInterfaceProps) {
 28→  const [input, setInput] = useState('');
 29→  const messagesEndRef = useRef<HTMLDivElement>(null);
 30→  const textareaRef = useRef<HTMLTextAreaElement>(null);
 31→
 32→  useEffect(() => {
 33→    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
 34→  }, [messages, isTyping]);
 35→
 36→  const handleSend = () => {
 37→    const trimmed = input.trim();
 38→    if (!trimmed || disabled || isTyping) return;
 39→    onSendMessage(trimmed);
 40→    setInput('');
 41→    if (textareaRef.current) {
 42→      textareaRef.current.style.height = 'auto';
 43→    }
 44→  };
 45→
 46→  const handleKeyDown = (e: React.KeyboardEvent) => {
 47→    if (e.key === 'Enter' && !e.shiftKey) {
 48→      e.preventDefault();
 49→      handleSend();
 50→    }
 51→  };
 52→
 53→  const handleInput = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
 54→    setInput(e.target.value);
 55→    // Auto-resize
 56→    const el = e.target;
 57→    el.style.height = 'auto';
 58→    el.style.height = Math.min(el.scrollHeight, 150) + 'px';
 59→  };
 60→
 61→  return (
 62→    <div className="flex flex-col h-full">
 63→      {/* Messages */}
 64→      <div className="flex-1 overflow-y-auto p-4 space-y-4">
 65→        {messages.length === 0 && (
 66→          <div className="text-center py-12">
 67→            <Bot className="h-10 w-10 text-slate-300 mx-auto mb-3" />
 68→            <h3 className="text-sm font-medium text-slate-700 mb-1">
 69→              Start a conversation
 70→            </h3>
 71→            <p className="text-xs text-slate-500 max-w-sm mx-auto">
 72→              Describe what you&apos;re working on. I&apos;ll help you frame the
 73→              problem, understand the user perspective, and define validation
 74→              criteria.
 75→            </p>
 76→          </div>
 77→        )}
 78→
 79→        {messages.map((msg) => (
 80→          <div
 81→            key={msg.id}
 82→            className={cn(
 83→              'flex gap-3',
 84→              msg.role === 'user' ? 'justify-end' : 'justify-start'
 85→            )}
 86→          >
 87→            {msg.role === 'assistant' && (
 88→              <div className="flex-shrink-0 flex flex-col items-center gap-0.5 mt-0.5">
 89→                <div className="h-7 w-7 rounded-full bg-violet-100 flex items-center justify-center">
 90→                  <Bot className="h-4 w-4 text-violet-600" />
 91→                </div>
 92→                <span className="text-[10px] text-slate-400">{botName}</span>
 93→              </div>
 94→            )}
 95→            <div className="flex flex-col items-end gap-1">
 96→              <div
 97→                className={cn(
 98→                  'max-w-[80%] rounded-xl px-4 py-2.5 text-sm leading-relaxed',
 99→                  msg.role === 'user'
100→                    ? msg.status === 'failed'
101→                      ? 'bg-red-900 text-white'
102→                      : 'bg-slate-900 text-white'
103→                    : 'bg-slate-100 text-slate-800'
104→                )}
105→              >
106→                <p className="whitespace-pre-wrap">{msg.content}</p>
107→              </div>
108→              {msg.status === 'failed' && (
109→                <div className="flex items-center gap-1.5">
110→                  <span className="text-[11px] text-red-500">Failed to send</span>
111→                  {onRetryMessage && (
112→                    <button
113→                      onClick={() => onRetryMessage(msg.id)}
114→                      className="flex items-center gap-1 text-[11px] text-red-600 hover:text-red-700 font-medium"
115→                    >
116→                      <RotateCcw className="h-3 w-3" />
117→                      Retry
118→                    </button>
119→                  )}
120→                </div>
121→              )}
122→            </div>
123→            {msg.role === 'user' && (
124→              <div className="flex-shrink-0 flex flex-col items-center gap-0.5 mt-0.5">
125→                <div className="h-7 w-7 rounded-full bg-slate-200 flex items-center justify-center">
126→                  <User className="h-4 w-4 text-slate-600" />
127→                </div>
128→                {userName && (
129→                  <span className="text-[10px] text-slate-400">{userName.split(' ')[0]}</span>
130→                )}
131→              </div>
132→            )}
133→          </div>
134→        ))}
135→
136→        {isTyping && (
137→          <div className="flex gap-3">
138→            <div className="flex-shrink-0 h-7 w-7 rounded-full bg-violet-100 flex items-center justify-center mt-0.5">
139→              <Bot className="h-4 w-4 text-violet-600" />
140→            </div>
141→            <div className="bg-slate-100 rounded-xl px-4 py-2.5">
142→              <div className="flex gap-1">
143→                <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
144→                <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
145→                <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
146→              </div>
147→            </div>
148→          </div>
149→        )}
150→
151→        <div ref={messagesEndRef} />
152→      </div>
153→
154→      {/* Input */}
155→      <div className="flex-shrink-0 border-t border-slate-200 p-4">
156→        <div className="flex items-end gap-2">
157→          <textarea
158→            ref={textareaRef}
159→            value={input}
160→            onChange={handleInput}
161→            onKeyDown={handleKeyDown}
162→            placeholder="Describe what you're working on..."
163→            disabled={disabled || isTyping}
164→            rows={1}
165→            className="flex-1 resize-none rounded-xl border border-slate-200 bg-white px-4 py-2.5 text-sm focus:outline-none focus:ring-2 focus:ring-violet-500 focus:border-transparent disabled:opacity-50 disabled:cursor-not-allowed"
166→          />
167→          <Button
168→            size="sm"
169→            onClick={handleSend}
170→            disabled={!input.trim() || disabled || isTyping}
171→            className="h-10 w-10 rounded-xl p-0"
172→          >
