  1→import { create } from 'zustand';
  2→import {
  3→  Conversation,
  4→  ConversationMessage,
  5→  ConversationPurpose,
  6→  ConversationState,
  7→  ConversationListItem,
  8→  KnowledgeSearchResult,
  9→} from '@/types';
 10→import { getAPIClient } from '@/lib/api';
 11→import { useProjectStore } from './projectStore';
 12→
 13→interface ConversationStore {
 14→  // Data
 15→  activeConversation: Conversation | null;
 16→  conversations: ConversationListItem[];
 17→  relevantKnowledge: Array<Record<string, unknown>>;
 18→
 19→  // UI State
 20→  isTyping: boolean;
 21→  isLoading: boolean;
 22→  isPreviewing: boolean;
 23→  previewContent: Record<string, string> | null;
 24→  previewMessageCount: number;
 25→  error: string | null;
 26→
 27→  // Actions
 28→  startConversation: (owner: string, purpose?: ConversationPurpose, frameId?: string) => Promise<Conversation>;
 29→  sendMessage: (content: string) => Promise<void>;
 30→  retryMessage: (messageId: string) => Promise<void>;
 31→  previewFrame: () => Promise<void>;
 32→  clearPreview: () => void;
 33→  synthesizeFrame: () => Promise<string | null>;
 34→  summarizeReview: () => Promise<string | null>;
 35→  loadConversation: (id: string) => Promise<void>;
 36→  loadConversations: (owner?: string) => Promise<void>;
 37→  clearConversation: () => void;
 38→  setError: (error: string | null) => void;
 39→}
 40→
 41→function transformConversationResponse(response: any): Conversation {
 42→  return {
 43→    id: response.id,
 44→    owner: response.owner,
 45→    status: response.status,
 46→    purpose: response.purpose || 'authoring',
 47→    frameId: response.frame_id,
 48→    projectId: response.project_id,
 49→    messages: response.messages.map((m: any) => ({
 50→      id: m.id,
 51→      role: m.role,
 52→      content: m.content,
 53→      timestamp: m.timestamp,
 54→      metadata: m.metadata,
 55→    })),
 56→    state: {
 57→      frameType: response.state.frame_type,
 58→      sectionsCovered: {
 59→        problemStatement: response.state.sections_covered?.problem_statement ?? 0,
 60→        userPerspective: response.state.sections_covered?.user_perspective ?? 0,
 61→        engineeringFraming: response.state.sections_covered?.engineering_framing ?? 0,
 62→        validationThinking: response.state.sections_covered?.validation_thinking ?? 0,
 63→      },
 64→      gaps: response.state.gaps || [],
 65→      readyToSynthesize: response.state.ready_to_synthesize || false,
 66→    },
 67→    createdAt: response.created_at,
 68→    updatedAt: response.updated_at,
 69→  };
 70→}
 71→
 72→function transformStateResponse(state: any): ConversationState {
 73→  return {
 74→    frameType: state.frame_type,
 75→    sectionsCovered: {
 76→      problemStatement: state.sections_covered?.problem_statement ?? 0,
 77→      userPerspective: state.sections_covered?.user_perspective ?? 0,
 78→      engineeringFraming: state.sections_covered?.engineering_framing ?? 0,
 79→      validationThinking: state.sections_covered?.validation_thinking ?? 0,
 80→    },
 81→    gaps: state.gaps || [],
 82→    readyToSynthesize: state.ready_to_synthesize || false,
 83→  };
 84→}
 85→
 86→export const useConversationStore = create<ConversationStore>()((set, get) => ({
 87→  activeConversation: null,
 88→  conversations: [],
 89→  relevantKnowledge: [],
 90→  isTyping: false,
 91→  isLoading: false,
 92→  isPreviewing: false,
 93→  previewContent: null,
 94→  previewMessageCount: 0,
 95→  error: null,
 96→
 97→  setError: (error) => set({ error }),
 98→
 99→  startConversation: async (owner, purpose?, frameId?) => {
100→    set({ isLoading: true, error: null });
101→    try {
102→      const api = getAPIClient();
103→      const projectId = useProjectStore.getState().currentProjectId;
104→      const response = await api.startConversation(owner, purpose, frameId, projectId ?? undefined);
105→      const conv = transformConversationResponse(response);
106→      set({ activeConversation: conv, isLoading: false, relevantKnowledge: [] });
107→      return conv;
108→    } catch (err) {
109→      set({
110→        error: err instanceof Error ? err.message : 'Failed to start conversation',
111→        isLoading: false,
112→      });
113→      throw err;
114→    }
115→  },
116→
117→  sendMessage: async (content) => {
118→    const { activeConversation } = get();
119→    if (!activeConversation) return;
120→
121→    const wasSynthesized = activeConversation.status === 'synthesized';
122→
123→    // Optimistically add user message and reactivate if synthesized
124→    const userMsg: ConversationMessage = {
125→      id: `temp-${Date.now()}`,
126→      role: 'user',
127→      content,
128→      timestamp: new Date().toISOString(),
129→    };
130→
131→    set((s) => ({
132→      activeConversation: s.activeConversation
133→        ? {
134→            ...s.activeConversation,
135→            status: wasSynthesized ? 'active' : s.activeConversation.status,
136→            messages: [...s.activeConversation.messages, userMsg],
137→          }
138→        : null,
139→      isTyping: true,
140→      error: null,
141→      // Invalidate preview cache since conversation changed
142→      previewContent: null,
143→      previewMessageCount: 0,
144→    }));
145→
146→    try {
147→      const api = getAPIClient();
148→      const response = await api.sendConversationMessage(
149→        activeConversation.id,
150→        content
151→      );
152→
153→      const aiMsg: ConversationMessage = {
154→        id: response.ai_response.id,
155→        role: 'assistant',
156→        content: response.ai_response.content,
157→        timestamp: response.ai_response.timestamp,
158→        metadata: response.ai_response.metadata,
159→      };
160→
161→      const updatedState = transformStateResponse(response.state);
162→
163→      set((s) => ({
164→        activeConversation: s.activeConversation
165→          ? {
166→              ...s.activeConversation,
167→              messages: [
168→                // Replace temp user msg with real one, add AI response
169→                ...s.activeConversation.messages.filter(
170→                  (m) => m.id !== userMsg.id
171→                ),
172→                {
173→                  id: response.message.id,
174→                  role: 'user',
175→                  content: response.message.content,
176→                  timestamp: response.message.timestamp,
177→                  metadata: response.message.metadata,
178→                },
179→                aiMsg,
180→              ],
181→              state: updatedState,
182→            }
183→          : null,
184→        relevantKnowledge: response.relevant_knowledge || [],
185→        isTyping: false,
186→      }));
187→    } catch (err) {
188→      // Mark the user message as failed so the user can retry
189→      set((s) => ({
190→        activeConversation: s.activeConversation
191→          ? {
192→              ...s.activeConversation,
193→              messages: s.activeConversation.messages.map((m) =>
194→                m.id === userMsg.id ? { ...m, status: 'failed' as const } : m
195→              ),
196→            }
197→          : null,
198→        error: err instanceof Error ? err.message : 'Failed to send message',
199→        isTyping: false,
200→      }));
201→    }
202→  },
203→
204→  retryMessage: async (messageId) => {
205→    const { activeConversation } = get();
206→    if (!activeConversation) return;
207→
208→    const failedMsg = activeConversation.messages.find(
209→      (m) => m.id === messageId && m.status === 'failed'
210→    );
211→    if (!failedMsg) return;
212→
213→    // Remove the failed message and re-send
214→    set((s) => ({
215→      activeConversation: s.activeConversation
216→        ? {
217→            ...s.activeConversation,
218→            messages: s.activeConversation.messages.filter((m) => m.id !== messageId),
219→          }
220→        : null,
221→      error: null,
222→    }));
223→
224→    // Re-send through the normal sendMessage path
225→    await get().sendMessage(failedMsg.content);
226→  },
227→
228→  previewFrame: async () => {
229→    const { activeConversation, previewContent, previewMessageCount } = get();
230→    if (!activeConversation) return;
231→
232→    const currentMsgCount = activeConversation.messages.length;
233→
234→    // Reuse cached preview if no new messages since last preview
235→    if (previewContent && previewMessageCount === currentMsgCount) {
236→      return; // Modal will show from existing previewContent
237→    }
238→
239→    set({ isPreviewing: true, error: null });
240→    try {
241→      const api = getAPIClient();
242→      const response = await api.previewFrame(activeConversation.id);
243→      set({
244→        previewContent: response.content,
245→        previewMessageCount: currentMsgCount,
246→        isPreviewing: false,
247→      });
248→    } catch (err) {
249→      set({
250→        error: err instanceof Error ? err.message : 'Failed to preview frame',
251→        isPreviewing: false,
252→      });
253→    }
254→  },
255→
256→  clearPreview: () => set({ previewContent: null }),
257→
258→  synthesizeFrame: async () => {
259→    const { activeConversation, previewContent, previewMessageCount } = get();
260→    if (!activeConversation) return null;
261→
262→    // Reuse cached preview content if still fresh (no new messages since preview)
263→    const currentMsgCount = activeConversation.messages.length;
264→    const cachedContent =
265→      previewContent && previewMessageCount === currentMsgCount
266→        ? previewContent
267→        : undefined;
268→
269→    set({ isLoading: true, error: null });
270→    try {
271→      const api = getAPIClient();
272→      const response = await api.synthesizeFrame(activeConversation.id, cachedContent);
273→
274→      set((s) => ({
275→        activeConversation: s.activeConversation
276→          ? {
277→              ...s.activeConversation,
278→              status: 'synthesized',
279→              frameId: response.frame_id,
280→            }
281→          : null,
282→        isLoading: false,
283→        previewContent: null,
284→        previewMessageCount: 0,
285→      }));
286→
287→      return response.frame_id;
288→    } catch (err) {
289→      set({
290→        error: err instanceof Error ? err.message : 'Failed to synthesize frame',
291→        isLoading: false,
292→      });
293→      return null;
294→    }
295→  },
296→
297→  summarizeReview: async () => {
298→    const { activeConversation } = get();
299→    if (!activeConversation) return null;
300→
301→    set({ isLoading: true, error: null });
302→    try {
303→      const api = getAPIClient();
304→      await api.summarizeReview(activeConversation.id);
305→      set({ isLoading: false });
306→      return activeConversation.frameId;
307→    } catch (err) {
308→      set({
309→        error: err instanceof Error ? err.message : 'Failed to summarize review',
310→        isLoading: false,
311→      });
312→      return null;
313→    }
314→  },
315→
316→  loadConversation: async (id) => {
317→    set({ isLoading: true, error: null });
318→    try {
319→      const api = getAPIClient();
320→      const response = await api.getConversation(id);
321→      const conv = transformConversationResponse(response);
322→      set({ activeConversation: conv, isLoading: false });
323→    } catch (err) {
324→      set({
325→        error: err instanceof Error ? err.message : 'Failed to load conversation',
326→        isLoading: false,
327→      });
328→    }
329→  },
330→
331→  loadConversations: async (owner?) => {
332→    set({ isLoading: true, error: null });
333→    try {
334→      const api = getAPIClient();
335→      const projectId = useProjectStore.getState().currentProjectId;
336→      const filters: Record<string, string> = {};
337→      if (owner) filters.owner = owner;
338→      if (projectId) filters.project_id = projectId;
339→      const response = await api.listConversations(
340→        Object.keys(filters).length > 0 ? filters : undefined
341→      );
342→      const conversations: ConversationListItem[] = response.map((item) => ({
343→        id: item.id,
344→        owner: item.owner,
345→        status: item.status as any,
346→        purpose: (item.purpose || 'authoring') as ConversationPurpose,
347→        frameId: item.frame_id,
348→        projectId: item.project_id,
349→        messageCount: item.message_count,
350→        updatedAt: item.updated_at,
351→      }));
352→      set({ conversations, isLoading: false });
353→    } catch (err) {
354→      set({
355→        error:
356→          err instanceof Error ? err.message : 'Failed to load conversations',
357→        isLoading: false,
358→      });
359→    }
360→  },
361→
362→  clearConversation: () =>
363→    set({ activeConversation: null, relevantKnowledge: [], previewContent: null, previewMessageCount: 0 }),
364→}));
