  1→'use client';
  2→
  3→import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
  4→import { useRouter, usePathname } from 'next/navigation';
  5→import { getAuthService, type AuthState } from '@/lib/api';
  6→
  7→interface AuthContextValue {
  8→  user: AuthState['user'] | null;
  9→  isAuthenticated: boolean;
 10→  isLoading: boolean;
 11→  error: string | null;
 12→  login: (email: string, password: string) => Promise<boolean>;
 13→  register: (data: {
 14→    email: string;
 15→    password: string;
 16→    passwordConfirm: string;
 17→    name?: string;
 18→  }) => Promise<boolean>;
 19→  logout: () => void;
 20→  clearError: () => void;
 21→}
 22→
 23→const AuthContext = createContext<AuthContextValue | null>(null);
 24→
 25→// Public routes that don't require authentication
 26→const PUBLIC_ROUTES = ['/login'];
 27→
 28→// Routes managed by their own auth context
 29→const isExcludedRoute = (path: string) => path.startsWith('/admin');
 30→
 31→export function AuthProvider({ children }: { children: React.ReactNode }) {
 32→  const auth = getAuthService();
 33→  const router = useRouter();
 34→  const pathname = usePathname();
 35→  const [user, setUser] = useState<AuthState['user'] | null>(null);
 36→  const [isLoading, setIsLoading] = useState(true);
 37→  const [error, setError] = useState<string | null>(null);
 38→
 39→  // Initialize auth state from storage and handle routing
 40→  useEffect(() => {
 41→    const initAuth = async () => {
 42→      const currentUser = auth.getUser();
 43→
 44→      if (isExcludedRoute(pathname)) {
 45→        // Skip user auth for admin routes (they have their own auth)
 46→        setIsLoading(false);
 47→        return;
 48→      }
 49→
 50→      if (currentUser) {
 51→        // Try to refresh token if we have one
 52→        const success = await auth.refresh();
 53→        if (success) {
 54→          setUser(auth.getUser());
 55→        } else {
 56→          setUser(null);
 57→          // Redirect to login if on protected route
 58→          if (!PUBLIC_ROUTES.includes(pathname)) {
 59→            router.replace('/login');
 60→          }
 61→        }
 62→      } else {
 63→        // No user - redirect to login if on protected route
 64→        if (!PUBLIC_ROUTES.includes(pathname)) {
 65→          router.replace('/login');
 66→        }
 67→      }
 68→
 69→      setIsLoading(false);
 70→    };
 71→
 72→    initAuth();
 73→  }, []);
 74→
 75→  // Handle route protection when pathname changes
 76→  useEffect(() => {
 77→    if (isLoading) return;
 78→
 79→    const isPublicRoute = PUBLIC_ROUTES.includes(pathname);
 80→
 81→    if (!user && !isPublicRoute && !isExcludedRoute(pathname)) {
 82→      router.replace('/login');
 83→    } else if (user && pathname === '/login') {
 84→      router.replace('/dashboard');
 85→    }
 86→  }, [pathname, user, isLoading, router]);
 87→
 88→  const login = useCallback(async (email: string, password: string): Promise<boolean> => {
 89→    setIsLoading(true);
 90→    setError(null);
 91→    try {
 92→      const loggedInUser = await auth.login(email, password);
 93→      setUser(loggedInUser);
 94→      setIsLoading(false);
 95→      router.replace('/dashboard');
 96→      return true;
 97→    } catch (err) {
 98→      setError(err instanceof Error ? err.message : 'Login failed');
 99→      setIsLoading(false);
100→      return false;
101→    }
102→  }, [auth, router]);
103→
104→  const register = useCallback(async (data: {
105→    email: string;
106→    password: string;
107→    passwordConfirm: string;
108→    name?: string;
109→  }): Promise<boolean> => {
110→    setIsLoading(true);
111→    setError(null);
112→    try {
113→      const registeredUser = await auth.register(data);
114→      setUser(registeredUser);
115→      setIsLoading(false);
116→      router.replace('/dashboard');
117→      return true;
118→    } catch (err) {
119→      setError(err instanceof Error ? err.message : 'Registration failed');
120→      setIsLoading(false);
121→      return false;
122→    }
123→  }, [auth, router]);
124→
125→  const logout = useCallback(() => {
126→    auth.logout();
127→    setUser(null);
128→    router.replace('/login');
129→  }, [auth, router]);
130→
131→  const clearError = useCallback(() => {
132→    setError(null);
133→  }, []);
134→
135→  // Show loading state while initializing
136→  if (isLoading) {
137→    return (
138→      <div className="flex h-screen items-center justify-center">
139→        <div className="animate-pulse text-slate-500">Loading...</div>
140→      </div>
141→    );
142→  }
143→
144→  return (
145→    <AuthContext.Provider
146→      value={{
147→        user,
148→        isAuthenticated: user !== null,
149→        isLoading,
150→        error,
151→        login,
152→        register,
153→        logout,
154→        clearError,
155→      }}
156→    >
157→      {children}
158→    </AuthContext.Provider>
159→  );
160→}
161→
162→export function useAuthContext() {
163→  const context = useContext(AuthContext);
164→  if (!context) {
165→    throw new Error('useAuthContext must be used within an AuthProvider');
166→  }
167→  return context;
168→}
