  1→'use client';
  2→
  3→import React from 'react';
  4→import { Frame, FrameStatus, FrameType } from '@/types';
  5→import { FrameCard } from './FrameCard';
  6→import { useAuthContext } from '@/contexts/AuthContext';
  7→import { cn } from '@/lib/utils';
  8→
  9→interface KanbanColumn {
 10→  status: FrameStatus;
 11→  title: string;
 12→  bgColor?: string;
 13→}
 14→
 15→const columns: KanbanColumn[] = [
 16→  { status: 'draft', title: 'Draft' },
 17→  { status: 'in_review', title: 'In Review' },
 18→  { status: 'ready', title: 'Ready' },
 19→  { status: 'feedback', title: 'Feedback', bgColor: 'bg-violet-50/70' },
 20→];
 21→
 22→interface KanbanBoardProps {
 23→  frames: Frame[];
 24→  typeFilter: FrameType | 'all';
 25→  ownerFilter: string;
 26→  searchQuery: string;
 27→  onFrameClick: (frameId: string) => void;
 28→}
 29→
 30→export function KanbanBoard({
 31→  frames,
 32→  typeFilter,
 33→  ownerFilter,
 34→  searchQuery,
 35→  onFrameClick,
 36→}: KanbanBoardProps) {
 37→  const { user } = useAuthContext();
 38→
 39→  // Filter frames
 40→  const filteredFrames = frames.filter((frame) => {
 41→    if (typeFilter !== 'all' && frame.type !== typeFilter) return false;
 42→    if (ownerFilter !== 'all' && frame.ownerId !== ownerFilter) return false;
 43→    if (searchQuery) {
 44→      const query = searchQuery.toLowerCase();
 45→      const matchesTitle = frame.problemStatement.toLowerCase().includes(query);
 46→      if (!matchesTitle) return false;
 47→    }
 48→    return true;
 49→  });
 50→
 51→  // Group by status
 52→  const framesByStatus = columns.reduce((acc, col) => {
 53→    acc[col.status] = filteredFrames.filter((f) => f.status === col.status);
 54→    return acc;
 55→  }, {} as Record<FrameStatus, Frame[]>);
 56→
 57→  // Get owner info - in production, this would come from an API
 58→  const getOwnerInfo = (ownerId: string) => {
 59→    // If the owner is the current user, use their info
 60→    if (user && ownerId === user.id) {
 61→      return {
 62→        name: user.name || user.email.split('@')[0],
 63→        initials: user.name?.[0]?.toUpperCase() || user.email[0].toUpperCase(),
 64→      };
 65→    }
 66→    // For other users, we'd need to fetch from API - for now use placeholder
 67→    return {
 68→      name: 'Team Member',
 69→      initials: 'TM',
 70→    };
 71→  };
 72→
 73→  return (
 74→    <div className="flex gap-4 h-full overflow-x-auto pb-4">
 75→      {columns.map((column) => (
 76→        <KanbanColumnComponent
 77→          key={column.status}
 78→          column={column}
 79→          frames={framesByStatus[column.status]}
 80→          getOwnerInfo={getOwnerInfo}
 81→          onFrameClick={onFrameClick}
 82→        />
 83→      ))}
 84→    </div>
 85→  );
 86→}
 87→
 88→interface KanbanColumnComponentProps {
 89→  column: KanbanColumn;
 90→  frames: Frame[];
 91→  getOwnerInfo: (ownerId: string) => { name: string; initials: string };
 92→  onFrameClick: (frameId: string) => void;
 93→}
 94→
 95→function KanbanColumnComponent({ column, frames, getOwnerInfo, onFrameClick }: KanbanColumnComponentProps) {
 96→  return (
 97→    <div
 98→      className={cn(
 99→        'flex-shrink-0 w-72 rounded-xl p-3',
100→        column.bgColor || 'bg-slate-100/50'
101→      )}
102→    >
103→      {/* Column Header */}
104→      <div className="flex items-center justify-between mb-3 px-1">
105→        <div className="flex items-center gap-2">
106→          <h3 className="text-sm font-semibold text-slate-700">
107→            {column.title}
108→          </h3>
109→          <span
110→            className={cn(
111→              'text-xs font-medium px-2 py-0.5 rounded-full',
112→              column.status === 'feedback'
113→                ? 'bg-violet-200 text-violet-800'
114→                : 'bg-slate-200 text-slate-600'
115→            )}
116→          >
117→            {frames.length}
118→          </span>
119→        </div>
120→      </div>
121→
122→      {/* Cards */}
123→      <div className="space-y-3">
124→        {frames.map((frame) => {
125→          const ownerInfo = getOwnerInfo(frame.ownerId);
126→          return (
127→            <FrameCard
128→              key={frame.id}
129→              frame={frame}
130→              ownerName={ownerInfo.name}
131→              ownerInitials={ownerInfo.initials}
132→              onClick={() => onFrameClick(frame.id)}
133→            />
134→          );
135→        })}
136→
137→        {frames.length === 0 && (
138→          <div className="text-center py-8 text-sm text-slate-400">
139→            No frames
140→          </div>
141→        )}
142→      </div>
143→    </div>
144→  );
145→}
