  42→function formatHistoryTimestamp(ts: string): string {
  43→  const date = new Date(ts);
  44→  const now = new Date();
  45→  const diffMs = now.getTime() - date.getTime();
  46→  const diffMin = Math.floor(diffMs / 60000);
  47→  if (diffMin < 1) return 'just now';
  48→  if (diffMin < 60) return `${diffMin}m ago`;
  49→  const diffHrs = Math.floor(diffMin / 60);
  50→  if (diffHrs < 24) return `${diffHrs}h ago`;
  51→  const diffDays = Math.floor(diffHrs / 24);
  52→  if (diffDays < 7) return `${diffDays}d ago`;
  53→  return date.toLocaleDateString();
  54→}
