  1→import { create } from 'zustand';
  2→import { persist } from 'zustand/middleware';
  3→import { Frame, FrameSection, AIConfig, FrameType, FrameStatus, ReviewComment, FrameFeedback, AppSpace } from '@/types';
  4→import { getAPIClient, transformFrameResponse, transformFrameToContent, transformAIEvaluation } from '@/lib/api';
  5→import { useProjectStore } from './projectStore';
  6→
  7→interface FrameStore {
  8→  // Data
  9→  frames: Frame[];
 10→
 11→  // UI State
 12→  selectedFrameId: string | null;
 13→  focusedSection: FrameSection | null;
 14→  currentSpace: AppSpace;
 15→
 16→  // Loading & Error State
 17→  isLoading: boolean;
 18→  error: string | null;
 19→
 20→  // Track unsaved frames (frames created but not explicitly saved)
 21→  unsavedFrameIds: Set<string>;
 22→
 23→  // AI Config
 24→  aiConfig: AIConfig | null;
 25→
 26→  // Actions
 27→  setSelectedFrame: (id: string | null) => void;
 28→  setFocusedSection: (section: FrameSection | null) => void;
 29→  setCurrentSpace: (space: AppSpace) => void;
 30→  setAIConfig: (config: AIConfig | null) => void;
 31→  setError: (error: string | null) => void;
 32→
 33→  // Frame CRUD
 34→  createFrame: (type: FrameType, ownerId: string) => Promise<Frame>;
 35→  updateFrame: (id: string, updates: Partial<Frame>) => Promise<void>;
 36→  deleteFrame: (id: string) => Promise<void>;
 37→  saveFrame: (id: string) => Promise<void>;
 38→  discardUnsavedFrame: (id: string) => void;
 39→  isFrameSaved: (id: string) => boolean;
 40→  loadFrames: () => Promise<void>;
 41→
 42→  // Frame Status
 43→  submitForReview: (id: string, reviewerId?: string) => Promise<void>;
 44→  markAsReady: (id: string) => Promise<void>;
 45→  startFeedback: (id: string) => Promise<void>;
 46→  submitFeedback: (id: string, feedback: FrameFeedback) => Promise<void>;
 47→
 48→  // AI Operations
 49→  evaluateFrame: (id: string) => Promise<void>;
 50→
 51→  // Comments
 52→  addComment: (frameId: string, section: FrameSection, content: string, authorId: string) => Promise<void>;
 53→
 54→  // Get helpers
 55→  getFrame: (id: string) => Frame | undefined;
 56→  getFramesByStatus: (status: FrameStatus) => Frame[];
 57→  getWorkingFrames: () => Frame[];
 58→  getArchivedFrames: () => Frame[];
 59→}
 60→
 61→export const useFrameStore = create<FrameStore>()(
 62→  persist(
 63→    (set, get) => ({
 64→      // Initial data - empty, will be loaded from API
 65→      frames: [],
 66→
 67→      // UI State
 68→      selectedFrameId: null,
 69→      focusedSection: null,
 70→      currentSpace: 'working',
 71→
 72→      // Loading & Error State
 73→      isLoading: false,
 74→      error: null,
 75→
 76→      // Track unsaved frames
 77→      unsavedFrameIds: new Set(),
 78→
 79→      // AI Config
 80→      aiConfig: null,
 81→
 82→      // Actions
 83→      setSelectedFrame: (id) => set({ selectedFrameId: id }),
 84→      setFocusedSection: (section) => set({ focusedSection: section }),
 85→      setCurrentSpace: (space) => set({ currentSpace: space, selectedFrameId: null }),
 86→      setAIConfig: (config) => set({ aiConfig: config }),
 87→      setError: (error) => set({ error }),
 88→
 89→      // Load frames from API
 90→      loadFrames: async () => {
 91→        set({ isLoading: true, error: null });
 92→        try {
 93→          const api = getAPIClient();
 94→          const projectId = useProjectStore.getState().currentProjectId;
 95→          const frameList = await api.listFrames(
 96→            projectId ? { project_id: projectId } : undefined
 97→          );
 98→
 99→          // Load full details for each frame
100→          const frames: Frame[] = [];
101→          for (const item of frameList) {
102→            try {
103→              const response = await api.getFrame(item.id);
104→              frames.push(transformFrameResponse(response));
105→            } catch {
106→              // Skip frames that fail to load
107→              console.warn(`Failed to load frame ${item.id}`);
108→            }
109→          }
110→
111→          set({ frames, isLoading: false });
112→        } catch (err) {
113→          set({
114→            error: err instanceof Error ? err.message : 'Failed to load frames',
115→            isLoading: false,
116→          });
117→        }
118→      },
119→
120→      // Frame CRUD
121→      createFrame: async (type, ownerId) => {
122→        set({ isLoading: true, error: null });
123→        try {
124→          const api = getAPIClient();
125→          const projectId = useProjectStore.getState().currentProjectId;
126→          const response = await api.createFrame({
127→            type,
128→            owner: ownerId,
129→            project_id: projectId ?? undefined,
130→          });
131→          const newFrame = transformFrameResponse(response);
132→
133→          set((s) => ({
134→            frames: [...s.frames, newFrame],
135→            selectedFrameId: newFrame.id,
136→            isLoading: false,
137→          }));
138→
139→          return newFrame;
140→        } catch (err) {
141→          set({
142→            error: err instanceof Error ? err.message : 'Failed to create frame',
143→            isLoading: false,
144→          });
145→          throw err;
146→        }
147→      },
148→
149→      updateFrame: async (id, updates) => {
150→        // Update local state first (optimistic update)
151→        set((s) => ({
152→          frames: s.frames.map((f) =>
153→            f.id === id
154→              ? { ...f, ...updates, updatedAt: new Date() }
155→              : f
156→          ),
157→        }));
158→
159→        try {
160→          const frame = get().getFrame(id);
161→          if (!frame) return;
162→
163→          const api = getAPIClient();
164→          await api.updateFrame(id, transformFrameToContent(frame));
165→        } catch (err) {
166→          // Revert on error
167→          set({ error: err instanceof Error ? err.message : 'Failed to update frame' });
168→        }
169→      },
170→
171→      deleteFrame: async (id) => {
172→        set({ isLoading: true, error: null });
173→        try {
174→          const api = getAPIClient();
175→          await api.deleteFrame(id);
176→        } catch (err) {
177→          set({
178→            error: err instanceof Error ? err.message : 'Failed to delete frame',
179→            isLoading: false,
180→          });
181→          return;
182→        }
183→
184→        set((s) => {
185→          const newUnsaved = new Set(s.unsavedFrameIds);
186→          newUnsaved.delete(id);
187→          return {
188→            frames: s.frames.filter((f) => f.id !== id),
189→            selectedFrameId: s.selectedFrameId === id ? null : s.selectedFrameId,
190→            unsavedFrameIds: newUnsaved,
191→            isLoading: false,
192→          };
193→        });
194→      },
195→
196→      saveFrame: async (id) => {
197→        const frame = get().getFrame(id);
198→        if (!frame) return;
199→
200→        set({ isLoading: true, error: null });
201→        try {
202→          const api = getAPIClient();
203→          await api.updateFrame(id, transformFrameToContent(frame));
204→        } catch (err) {
205→          set({
206→            error: err instanceof Error ? err.message : 'Failed to save frame',
207→            isLoading: false,
208→          });
209→          return;
210→        }
211→
212→        set((s) => {
213→          const newUnsaved = new Set(s.unsavedFrameIds);
214→          newUnsaved.delete(id);
215→          return {
216→            unsavedFrameIds: newUnsaved,
217→            isLoading: false,
218→          };
219→        });
220→      },
221→
222→      discardUnsavedFrame: (id) => {
223→        const state = get();
224→        if (state.unsavedFrameIds.has(id)) {
225→          get().deleteFrame(id);
226→        }
227→      },
228→
229→      isFrameSaved: (id) => {
230→        return !get().unsavedFrameIds.has(id);
231→      },
232→
233→      // Frame Status
234→      submitForReview: async (id, reviewerId?) => {
235→        const frame = get().getFrame(id);
236→        if (!frame) return;
237→
238→        // Mark as saved first
239→        await get().saveFrame(id);
240→
241→        set({ isLoading: true, error: null });
242→        try {
243→          const api = getAPIClient();
244→
245→          // Assign reviewer if provided
246→          if (reviewerId) {
247→            await api.updateFrameMeta(id, { reviewer: reviewerId });
248→          }
249→
250→          // Update status via API
251→          await api.updateFrameStatus(id, 'in_review');
252→
253→          // Evaluate frame with AI
254→          const evaluation = await api.evaluateFrame(id);
255→          const transformed = transformAIEvaluation(evaluation);
256→
257→          set((s) => ({
258→            frames: s.frames.map((f) =>
259→              f.id === id
260→                ? {
261→                    ...f,
262→                    status: 'in_review' as FrameStatus,
263→                    aiScore: transformed.score,
264→                    aiScoreBreakdown: transformed.breakdown,
265→                    aiIssues: transformed.issues,
266→                    aiFeedback: transformed.feedback,
267→                    updatedAt: new Date(),
268→                  }
269→                : f
270→            ),
271→            isLoading: false,
272→          }));
273→        } catch (err) {
274→          set({
275→            error: err instanceof Error ? err.message : 'Failed to submit for review',
276→            isLoading: false,
277→          });
278→        }
279→      },
280→
281→      markAsReady: async (id) => {
282→        set({ isLoading: true, error: null });
283→        try {
284→          const api = getAPIClient();
285→          await api.updateFrameStatus(id, 'ready');
286→          set({ isLoading: false });
287→        } catch (err) {
288→          set({
289→            error: err instanceof Error ? err.message : 'Failed to mark as ready',
290→            isLoading: false,
291→          });
292→          return;
293→        }
294→
295→        set((s) => ({
296→          frames: s.frames.map((f) =>
297→            f.id === id
298→              ? { ...f, status: 'ready' as FrameStatus, updatedAt: new Date() }
299→              : f
300→          ),
301→        }));
302→      },
303→
304→      startFeedback: async (id) => {
305→        set({ isLoading: true, error: null });
306→        try {
307→          const api = getAPIClient();
308→          await api.updateFrameStatus(id, 'feedback');
309→          set({ isLoading: false });
310→        } catch (err) {
311→          set({
312→            error: err instanceof Error ? err.message : 'Failed to start feedback',
313→            isLoading: false,
314→          });
315→          return;
316→        }
317→
318→        set((s) => ({
319→          frames: s.frames.map((f) =>
320→            f.id === id
321→              ? { ...f, status: 'feedback' as FrameStatus, updatedAt: new Date() }
322→              : f
323→          ),
324→        }));
325→      },
326→
327→      submitFeedback: async (id, feedback) => {
328→        set({ isLoading: true, error: null });
329→        try {
330→          const api = getAPIClient();
331→          await api.submitFeedback(id, {
332→            outcome: feedback.outcome,
333→            summary: feedback.summary,
334→            lessons_learned: feedback.lessonsLearned,
335→          });
336→          set({ isLoading: false });
337→        } catch (err) {
338→          set({
339→            error: err instanceof Error ? err.message : 'Failed to submit feedback',
340→            isLoading: false,
341→          });
342→          return;
343→        }
344→
345→        set((s) => ({
346→          frames: s.frames.map((f) =>
347→            f.id === id
348→              ? {
349→                  ...f,
350→                  status: 'archived' as FrameStatus,
351→                  feedback,
352→                  updatedAt: new Date(),
353→                }
354→              : f
355→          ),
356→        }));
357→      },
358→
359→      // AI Operations
360→      evaluateFrame: async (id) => {
361→        set({ isLoading: true, error: null });
362→        try {
363→          const api = getAPIClient();
364→          const evaluation = await api.evaluateFrame(id);
365→          const transformed = transformAIEvaluation(evaluation);
366→
367→          set((s) => ({
368→            frames: s.frames.map((f) =>
369→              f.id === id
370→                ? {
371→                    ...f,
372→                    aiScore: transformed.score,
373→                    aiScoreBreakdown: transformed.breakdown,
374→                    aiIssues: transformed.issues,
375→                    aiFeedback: transformed.feedback,
376→                    updatedAt: new Date(),
377→                  }
378→                : f
379→            ),
380→            isLoading: false,
381→          }));
382→        } catch (err) {
383→          set({
384→            error: err instanceof Error ? err.message : 'Failed to evaluate frame',
385→            isLoading: false,
386→          });
387→        }
388→      },
389→
390→      // Comments
391→      addComment: async (frameId, section, content, authorId) => {
392→        const comment: ReviewComment = {
393→          id: `comment-${Date.now()}`,
394→          section,
395→          authorId,
396→          content,
397→          createdAt: new Date(),
398→        };
399→
400→        try {
401→          const api = getAPIClient();
402→          await api.addComment(frameId, {
403→            section,
404→            author: authorId,
405→            content,
406→          });
407→        } catch (err) {
408→          set({ error: err instanceof Error ? err.message : 'Failed to add comment' });
409→          return;
410→        }
411→
412→        set((s) => ({
413→          frames: s.frames.map((f) =>
414→            f.id === frameId
415→              ? {
416→                  ...f,
417→                  comments: [...(f.comments || []), comment],
418→                  updatedAt: new Date(),
419→                }
420→              : f
421→          ),
422→        }));
423→      },
424→
425→      // Get helpers
426→      getFrame: (id) => get().frames.find((f) => f.id === id),
427→      getFramesByStatus: (status) => get().frames.filter((f) => f.status === status),
428→      getWorkingFrames: () => get().frames.filter((f) => f.status !== 'archived'),
429→      getArchivedFrames: () => get().frames.filter((f) => f.status === 'archived'),
430→    }),
431→    {
432→      name: 'framer-storage',
433→      version: 2,
434→      partialize: (state) => ({
435→        frames: state.frames,
436→        aiConfig: state.aiConfig,
437→      }),
438→    }
439→  )
440→);
