385→  // ==================== User Endpoints ====================
386→
387→  /**
388→   * List users from PocketBase
389→   */
390→  async listUsers(): Promise<Array<{ id: string; name: string; email: string }>> {
391→    return this.request<Array<{ id: string; name: string; email: string }>>('/api/users');
392→  }
393→
394→  /**
395→   * Update frame metadata (reviewer, approver)
396→   */
397→  async updateFrameMeta(frameId: string, data: {
398→    reviewer?: string;
