208→ * Framer API Client class
209→ */
210→export class FramerAPIClient {
211→  private baseURL: string;
212→  private token: string | null = null;
213→
214→  constructor(baseURL: string = API_BASE_URL) {
215→    this.baseURL = baseURL;
216→  }
217→
218→  /**
219→   * Set authentication token
220→   */
221→  setToken(token: string | null) {
222→    this.token = token;
223→  }
224→
225→  /**
226→   * Get current token
227→   */
228→  getToken(): string | null {
229→    return this.token;
230→  }
231→
232→  /**
233→   * Make an API request
234→   */
235→  private async request<T>(endpoint: string, options: RequestOptions = {}): Promise<T> {
236→    const { method = 'GET', body, headers = {}, signal } = options;
237→
238→    const controller = new AbortController();
239→    const timeoutId = setTimeout(() => controller.abort(), API_TIMEOUT);
240→
241→    try {
242→      const requestHeaders: Record<string, string> = {
243→        'Content-Type': 'application/json',
244→        ...headers,
245→      };
246→
247→      if (this.token) {
248→        requestHeaders['Authorization'] = `Bearer ${this.token}`;
249→      }
250→
251→      const response = await fetch(`${this.baseURL}${endpoint}`, {
252→        method,
253→        headers: requestHeaders,
254→        body: body ? JSON.stringify(body) : undefined,
255→        signal: signal || controller.signal,
256→      });
257→
258→      clearTimeout(timeoutId);
259→
260→      if (!response.ok) {
261→        let detail: string | undefined;
262→        try {
263→          const errorData = await response.json();
264→          detail = errorData.detail;
265→        } catch {
266→          // Ignore JSON parse errors
267→        }
268→        throw new APIError(
269→          `API request failed: ${response.statusText}`,
270→          response.status,
271→          detail
272→        );
273→      }
274→
275→      // Handle 204 No Content
276→      if (response.status === 204) {
277→        return undefined as T;
278→      }
279→
280→      return await response.json();
281→    } catch (error) {
282→      clearTimeout(timeoutId);
283→      if (error instanceof APIError) {
284→        throw error;
285→      }
286→      if (error instanceof Error && error.name === 'AbortError') {
287→        throw new APIError('Request timeout', 408);
288→      }
289→      throw new APIError('Network error', 0, String(error));
290→    }
291→  }
