/**
 * Framer API Client
 *
 * Type-safe HTTP client for the FastAPI backend.
 */

import type {
  Frame,
  FrameType,
  FrameStatus,
  FrameSection,
  ConversationMessage,
  ConversationState,
  ConversationListItem,
  KnowledgeEntry,
  KnowledgeCategory,
  KnowledgeSource,
  KnowledgeSearchResult,
} from '@/types';

// API Configuration
const API_BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000';
const API_TIMEOUT = parseInt(process.env.NEXT_PUBLIC_API_TIMEOUT || '120000');

// API Error class
export class APIError extends Error {
  constructor(
    message: string,
    public status: number,
    public detail?: string
  ) {
    super(message);
    this.name = 'APIError';
  }
}

// Request options type
interface RequestOptions {
  method?: 'GET' | 'POST' | 'PUT' | 'PATCH' | 'DELETE';
  body?: unknown;
  headers?: Record<string, string>;
  signal?: AbortSignal;
}

// API Response types (matching backend models)
export interface FrameResponse {
  id: string;
  type: string;
  status: string;
  owner: string;
  content: {
    problem_statement: string;
    user_perspective: string;
    engineering_framing: string;
    validation_thinking: string;
  };
  meta: {
    created_at: string;
    updated_at: string;
    project_id: string | null;

